import React, { useState, useEffect } from 'react';

// Bomb mascot for challenge, explosion for game over
const BombMascot = () => (
  <div style={{ fontSize: '5rem', margin: '1.5rem 0' }} role="img" aria-label="bomb">
    üí£
  </div>
);
const Explosion = () => (
  <div style={{ fontSize: '5rem', margin: '1.5rem 0' }} role="img" aria-label="explosion">
    üí•
  </div>
);

// Strongly curved, hand-drawn SVG arrow for the start screen
const CurvedArrow = () => (
  <svg
    width="120"
    height="70"
    viewBox="0 0 120 70"
    style={{
      position: 'absolute',
      top: '-80px',
      left: '50%',
      transform: 'translateX(-50%)',
      pointerEvents: 'none',
      zIndex: 2
    }}
  >
    {/* Curved path */}
    <path
      d="M20,60 Q60,10 100,60"
      stroke="#222"
      strokeWidth="4"
      fill="none"
      strokeLinecap="round"
    />
    {/* Arrowhead */}
    <polygon points="92,56 100,70 108,56" fill="#222" />
  </svg>
);

type GameState = 'logo' | 'start' | 'challenge' | 'gameOver' | 'welcome';

interface Problem {
  question: string;
  choices: string[];
  correctAnswer: number;
}

const SAT_PROBLEMS: Problem[] = [
  {
    question: "What is 25% of 80?",
    choices: ["15", "18", "20", "25"],
    correctAnswer: 2
  },
  {
    question: "What is 1/4 of 60?",
    choices: ["10", "12", "15", "20"],
    correctAnswer: 2
  },
  {
    question: "If a shirt costs $40 and is discounted by 10%, what is the sale price?",
    choices: ["$36", "$38", "$34", "$32"],
    correctAnswer: 0
  },
  {
    question: "What is 30% of 50?",
    choices: ["10", "12", "15", "20"],
    correctAnswer: 2
  },
  {
    question: "What is 2/5 of 25?",
    choices: ["5", "8", "10", "12"],
    correctAnswer: 2
  },
  {
    question: "If you have 3/4 of a pizza and eat 1/2 of what you have, how much pizza is left?",
    choices: ["1/4", "3/8", "1/2", "3/4"],
    correctAnswer: 1
  },
  {
    question: "What is 60 increased by 20%?",
    choices: ["66", "70", "72", "80"],
    correctAnswer: 2
  },
  {
    question: "If 10% of x is 5, what is x?",
    choices: ["10", "20", "50", "100"],
    correctAnswer: 3
  },
  {
    question: "What is 2/3 of 21?",
    choices: ["12", "13", "14", "15"],
    correctAnswer: 2
  },
  {
    question: "If a number is decreased by 25% and the result is 60, what was the original number?",
    choices: ["75", "80", "85", "90"],
    correctAnswer: 1
  }
];

function App() {
  const [gameState, setGameState] = useState<GameState>('logo');
  const [timeLeft, setTimeLeft] = useState(10);
  const [currentProblem, setCurrentProblem] = useState<Problem | null>(null);
  const [selectedAnswer, setSelectedAnswer] = useState<number | null>(null);
  const [showResult, setShowResult] = useState(false);

  // Logo splash effect
  useEffect(() => {
    if (gameState === 'logo') {
      const timer = setTimeout(() => {
        setGameState('start');
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [gameState]);

  // Timer countdown
  useEffect(() => {
    if (gameState === 'challenge' && timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            clearInterval(timer);
            setGameState('gameOver');
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [gameState, timeLeft]);

  const handleStart = () => {
    const randomProblem = SAT_PROBLEMS[Math.floor(Math.random() * SAT_PROBLEMS.length)];
    setCurrentProblem(randomProblem);
    setTimeLeft(10);
    setSelectedAnswer(null);
    setShowResult(false);
    setGameState('challenge');
  };

  const handleAnswerSelect = (answerIndex: number) => {
    if (selectedAnswer !== null) return; // Prevent multiple selections

    setSelectedAnswer(answerIndex);
    setShowResult(true);

    setTimeout(() => {
      if (answerIndex === currentProblem!.correctAnswer) {
        setGameState('welcome');
      } else {
        setGameState('gameOver');
      }
    }, 1200);
  };

  const handleTryAgain = () => {
    setGameState('start');
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  if (gameState === 'logo') {
    return (
      <div className="app">
        <div className="logo">üí£ SAT BOMB üí£</div>
      </div>
    );
  }

  if (gameState === 'start') {
    return (
      <div className="app">
        <div className="start-screen">
          <div style={{ textAlign: 'center', color: '#2d3a3a', marginBottom: '2rem' }}>
            <h1 style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>Solve this problem to unlock the website!</h1>
            <p style={{ fontSize: '1.2rem', opacity: 0.8 }}>You have 10 seconds to defuse the bomb!</p>
          </div>
          <div style={{ position: 'relative', width: 'fit-content' }}>
            <button className="start-button" onClick={handleStart}>
              START
            </button>
            <CurvedArrow />
          </div>
        </div>
      </div>
    );
  }

  if (gameState === 'challenge' && currentProblem) {
    return (
      <div className="app">
        <div className="challenge-screen">
          <div className="timer">‚è∞ {formatTime(timeLeft)}</div>
          <div className="problem">
            {currentProblem.question}
          </div>
          <BombMascot />
          <div className="choices">
            {currentProblem.choices.map((choice, index) => (
              <button
                key={index}
                className={`choice ${
                  selectedAnswer !== null
                    ? index === currentProblem.correctAnswer
                      ? 'correct'
                      : index === selectedAnswer
                      ? 'incorrect'
                      : ''
                    : ''
                }`}
                onClick={() => handleAnswerSelect(index)}
                disabled={selectedAnswer !== null}
              >
                {String.fromCharCode(97 + index)} {choice}
              </button>
            ))}
          </div>
          {showResult && (
            <div style={{ color: '#2d3a3a', fontSize: '1.5rem', marginTop: '1rem' }}>
              {selectedAnswer === currentProblem.correctAnswer ? '‚úÖ Correct!' : '‚ùå Wrong!'}
            </div>
          )}
        </div>
      </div>
    );
  }

  if (gameState === 'gameOver') {
    return (
      <div className="app">
        <div className="game-over">
          <Explosion />
          <h2>üí• BOOM! üí•</h2>
          <p style={{ fontSize: '1.5rem', marginBottom: '2rem' }}>Oops! Try again!</p>
          <button className="try-again-button" onClick={handleTryAgain}>
            Try Again
          </button>
        </div>
      </div>
    );
  }

  if (gameState === 'welcome') {
    return (
      <div className="app">
        <div className="welcome-screen">
          <h1>üéâ Welcome to the Website! üéâ</h1>
          <p>You successfully defused the bomb!</p>
          <p style={{ marginTop: '1rem', fontSize: '1.2rem' }}>
            The main website content will be built here...
          </p>
        </div>
      </div>
    );
  }

  return null;
}

export default App;
