import React, { useState, useEffect } from 'react';

// Bomb mascot for challenge, explosion for game over
const BombMascot = () => (
  <div style={{ fontSize: '5rem', margin: '1.5rem 0' }} role="img" aria-label="bomb">
    💣
  </div>
);
const Explosion = () => (
  <div style={{ fontSize: '5rem', margin: '1.5rem 0' }} role="img" aria-label="explosion">
    💥
  </div>
);

type GameState = 'logo' | 'start' | 'challenge' | 'gameOver' | 'welcome';

interface Problem {
  question: string;
  choices: string[];
  correctAnswer: number;
}

const SAT_PROBLEMS: Problem[] = [
  {
    question: "What is 2 + 3?",
    choices: ["4", "5", "6", "7"],
    correctAnswer: 1
  },
  {
    question: "What is 10 - 4?",
    choices: ["5", "6", "7", "8"],
    correctAnswer: 1
  },
  {
    question: "What is 3 x 2?",
    choices: ["5", "6", "7", "8"],
    correctAnswer: 1
  },
  {
    question: "What is 12 ÷ 4?",
    choices: ["2", "3", "4", "5"],
    correctAnswer: 1
  },
  {
    question: "What is 10% of 50?",
    choices: ["2", "5", "10", "20"],
    correctAnswer: 1
  },
  {
    question: "What is 7 + 1?",
    choices: ["6", "7", "8", "9"],
    correctAnswer: 2
  },
  {
    question: "What is 9 - 3?",
    choices: ["5", "6", "7", "8"],
    correctAnswer: 1
  },
  {
    question: "What is 5 x 1?",
    choices: ["4", "5", "6", "7"],
    correctAnswer: 1
  },
  {
    question: "What is 8 ÷ 2?",
    choices: ["2", "3", "4", "5"],
    correctAnswer: 2
  },
  {
    question: "What is 6 + 2?",
    choices: ["7", "8", "9", "10"],
    correctAnswer: 1
  }
];

function App() {
  const [gameState, setGameState] = useState<GameState>('logo');
  const [timeLeft, setTimeLeft] = useState(10);
  const [currentProblem, setCurrentProblem] = useState<Problem | null>(null);
  const [selectedAnswer, setSelectedAnswer] = useState<number | null>(null);
  const [showResult, setShowResult] = useState(false);

  // Logo splash effect
  useEffect(() => {
    if (gameState === 'logo') {
      const timer = setTimeout(() => {
        setGameState('start');
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [gameState]);

  // Timer countdown
  useEffect(() => {
    if (gameState === 'challenge' && timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            clearInterval(timer);
            setGameState('gameOver');
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [gameState, timeLeft]);

  const handleStart = () => {
    const randomProblem = SAT_PROBLEMS[Math.floor(Math.random() * SAT_PROBLEMS.length)];
    setCurrentProblem(randomProblem);
    setTimeLeft(10);
    setSelectedAnswer(null);
    setShowResult(false);
    setGameState('challenge');
  };

  const handleAnswerSelect = (answerIndex: number) => {
    if (selectedAnswer !== null) return; // Prevent multiple selections

    setSelectedAnswer(answerIndex);
    setShowResult(true);

    setTimeout(() => {
      if (answerIndex === currentProblem!.correctAnswer) {
        setGameState('welcome');
      } else {
        setGameState('gameOver');
      }
    }, 1200);
  };

  const handleTryAgain = () => {
    setGameState('start');
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  if (gameState === 'logo') {
    return (
      <div className="app">
        <div className="logo">💣 SAT BOMB 💣</div>
      </div>
    );
  }

  if (gameState === 'start') {
    return (
      <div className="app">
        <div className="start-screen">
          <div style={{ textAlign: 'center', color: 'white', marginBottom: '2rem' }}>
            <h1 style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>Solve this problem to unlock the website!</h1>
            <p style={{ fontSize: '1.2rem', opacity: 0.8 }}>You have 10 seconds to defuse the bomb!</p>
          </div>
          <button className="start-button" onClick={handleStart}>
            <div className="arrow">⬇️</div>
            START
          </button>
        </div>
      </div>
    );
  }

  if (gameState === 'challenge' && currentProblem) {
    return (
      <div className="app">
        <div className="challenge-screen">
          <div className="timer">⏰ {formatTime(timeLeft)}</div>
          <div className="problem">
            {currentProblem.question}
          </div>
          <BombMascot />
          <div className="choices">
            {currentProblem.choices.map((choice, index) => (
              <button
                key={index}
                className={`choice ${
                  selectedAnswer !== null
                    ? index === currentProblem.correctAnswer
                      ? 'correct'
                      : index === selectedAnswer
                      ? 'incorrect'
                      : ''
                    : ''
                }`}
                onClick={() => handleAnswerSelect(index)}
                disabled={selectedAnswer !== null}
              >
                {String.fromCharCode(97 + index)} {choice}
              </button>
            ))}
          </div>
          {showResult && (
            <div style={{ color: 'white', fontSize: '1.5rem', marginTop: '1rem' }}>
              {selectedAnswer === currentProblem.correctAnswer ? '✅ Correct!' : '❌ Wrong!'}
            </div>
          )}
        </div>
      </div>
    );
  }

  if (gameState === 'gameOver') {
    return (
      <div className="app">
        <div className="game-over">
          <Explosion />
          <h2>💥 BOOM! 💥</h2>
          <p style={{ fontSize: '1.5rem', marginBottom: '2rem' }}>Oops! Try again!</p>
          <button className="try-again-button" onClick={handleTryAgain}>
            Try Again
          </button>
        </div>
      </div>
    );
  }

  if (gameState === 'welcome') {
    return (
      <div className="app">
        <div className="welcome-screen">
          <h1>🎉 Welcome to the Website! 🎉</h1>
          <p>You successfully defused the bomb!</p>
          <p style={{ marginTop: '1rem', fontSize: '1.2rem' }}>
            The main website content will be built here...
          </p>
        </div>
      </div>
    );
  }

  return null;
}

export default App;
