  );
}

// Helper to get/set passed levels in localStorage
function getPassedLevels() {
  try {
    return JSON.parse(localStorage.getItem('satifyPassedLevels') || '[]');
  } catch {
    return [];
  }
}
function setPassedLevels(levels: number[]) {
  localStorage.setItem('satifyPassedLevels', JSON.stringify(levels));
}

// Level splitting logic
const DIFFICULTY_ORDER = { easy: 1, medium: 2, hard: 3 };
const sortedQuestions = [...questions].sort((a, b) => DIFFICULTY_ORDER[a.difficulty] - DIFFICULTY_ORDER[b.difficulty]);
const LEVELS = Array.from({ length: 6 }, (_, i) => sortedQuestions.slice(i * 10, (i + 1) * 10));

// Level selection screen
function LevelSelect({ onSelect, passedLevels }: { onSelect: (level: number) => void, passedLevels: number[] }) {
  return (
    <div style={{ width: '100vw', minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', background: '#fff' }}>
      <h2 style={{ color: '#23a9fa', fontWeight: 800, fontSize: '2rem', marginBottom: 32 }}>Select a Level</h2>
      <div style={{ display: 'flex', gap: 24 }}>
        {LEVELS.map((level, idx) => {
          const passed = passedLevels.includes(idx + 1);
          const locked = idx > 0 && !passedLevels.includes(idx);
          return (
            <button
              key={idx}
              onClick={() => !locked && onSelect(idx + 1)}
              disabled={locked}
              style={{
                width: 70,
                height: 70,
                borderRadius: '50%',
                background: passed ? '#23a9fa' : locked ? '#e3f2fd' : '#fff',
                color: passed ? '#fff' : '#23a9fa',
                border: '2.5px solid #23a9fa',
                fontWeight: 800,
                fontSize: '1.3rem',
                margin: 0,
                boxShadow: passed ? '0 2px 12px #23a9fa22' : '0 2px 8px #23a9fa11',
                cursor: locked ? 'not-allowed' : 'pointer',
                position: 'relative',
                transition: 'background 0.18s',
              }}
            >
              {passed && (
                <span style={{ position: 'absolute', top: 8, right: 8, fontSize: 22 }}>‚úîÔ∏è</span>
              )}
              {idx + 1}
            </button>
          );
        })}
      </div>
    </div>
  );
}

// MathLevel component for a given level
function MathLevel({ level, onBack }: { level: number, onBack: () => void }) {
  const [loading, setLoading] = useState(true);
  const [current, setCurrent] = useState(0);
  const [selected, setSelected] = useState<number | null>(null);
  const [score, setScore] = useState(0);
  const [showFeedback, setShowFeedback] = useState(false);
  const [checking, setChecking] = useState(false);
  const [wasCorrect, setWasCorrect] = useState(false);
  const [startTimes, setStartTimes] = useState<number[]>([]);
  const [endTimes, setEndTimes] = useState<number[]>([]);
  const [passedLevels, setPassedLevelsState] = useState(getPassedLevels());
  const levelQuestions = LEVELS[level - 1] || [];
  useEffect(() => {
    setLoading(true);
    const timer = setTimeout(() => setLoading(false), 800);
    setStartTimes([Date.now()]);
    setEndTimes([]);
    setCurrent(0);
    setScore(0);
    setSelected(null);
    setShowFeedback(false);
    setWasCorrect(false);
    setChecking(false);
    return () => clearTimeout(timer);
  }, [level]);
  const q = levelQuestions[current];
  const canCheck = selected !== null && !showFeedback;
  const canContinue = showFeedback;
  // Track time for each question
  useEffect(() => {
    if (current < levelQuestions.length && startTimes.length === current) {
      setStartTimes(times => [...times, Date.now()]);
    }
  }, [current]);
  const handleCheck = () => {
    if (canCheck) {
      setChecking(true);
      setTimeout(() => {
        setShowFeedback(true);
        setWasCorrect(selected === q.answer);
        setChecking(false);
        if (selected === q.answer) setScore(s => s + 1);
        setEndTimes(times => [...times, Date.now()]);
      }, 200);
    } else if (canContinue) {
      setShowFeedback(false);
      setSelected(null);
      setWasCorrect(false);
      setCurrent(c => c + 1);
    }
  };
  if (loading) return <LoadingScreen message={`Loading Level ${level}...`} />;
  if (current >= levelQuestions.length) {
    // Calculate average time per question
    const times = endTimes.map((end, i) => (end - (startTimes[i] || end)) / 1000);
    const avg = times.length ? (times.reduce((a, b) => a + b, 0) / times.length) : 0;
    const passed = score >= 6;
    // Store passed level if passed
    useEffect(() => {
      if (passed && !passedLevels.includes(level)) {
        const newPassed = [...passedLevels, level];
        setPassedLevels(newPassed);
        setPassedLevelsState(newPassed);
      }
    }, []);
    return (
      <div style={{ textAlign: 'center', marginTop: 48 }}>
        <div style={{ fontSize: '1.5rem', color: passed ? '#23a9fa' : '#ff5a5a', fontWeight: 800, marginBottom: 16 }}>
          {passed ? 'Level Passed!' : 'Level Not Passed'}
        </div>
        <div style={{ fontSize: '1.2rem', color: '#222', marginBottom: 12 }}>
          Score: {score} / {levelQuestions.length}
        </div>
        <div style={{ fontSize: '1.1rem', color: '#1976d2', marginBottom: 24 }}>
          Average time per question: {avg.toFixed(2)}s
        </div>
        <button onClick={onBack} style={{ fontSize: '1.1rem', padding: '0.7rem 2rem', borderRadius: '20px', fontWeight: 700, color: '#2d3a3a', background: '#fff', border: '2px solid #23a9fa', marginTop: '0.5rem', cursor: 'pointer' }}>Back to Levels</button>
      </div>
    );
  }
  return (
    <div className="screen-transition" key={`mathLevel${level}`}> 
      <div className="app" style={{ position: 'relative', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'flex-start', minHeight: '100vh', width: '100vw', padding: 0, paddingBottom: showFeedback ? 180 : 70 }}>
        <ProgressBar current={Math.min(current + 1, levelQuestions.length)} total={levelQuestions.length} />
        <QuestionCard
          question={q.question}
          choices={q.choices}
          selected={selected}
          onSelect={idx => {
            if (!showFeedback) setSelected(idx);
          }}
          showFeedback={showFeedback}
          correct={wasCorrect}
          correctAnswer={q.answer}
          onCheck={handleCheck}
          canCheck={canCheck}
          canContinue={canContinue}
          checking={checking}
        />
      </div>
    </div>
  );
}

