function MainApp() {
  const [passedLevels, setPassedLevels] = useState(getPassedLevels());
  const [selectedLevel, setSelectedLevel] = useState<number | null>(null);
  useEffect(() => {
    setPassedLevels(getPassedLevels());
  }, [selectedLevel]);
  if (selectedLevel) {
    return <MathLevel level={selectedLevel} onBack={() => setSelectedLevel(null)} />;
  }
  return <LevelSelect onSelect={setSelectedLevel} passedLevels={passedLevels} />;
}

